package br.usp.ime.lapessc.xflow2.entity;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.Index;import javax.persistence.JoinColumn;import javax.persistence.ManyToOne;import javax.persistence.Table;import org.hibernate.annotations.OnDelete;import org.hibernate.annotations.OnDeleteAction;@Table(indexes = {@Index(name="file_path_index2", columnList="FILE_PATH")})@Entity(name="file_dependency")public class FileDependencyObject extends DependencyObject {	@ManyToOne	@OnDelete(action = OnDeleteAction.CASCADE)	@JoinColumn(name = "FILE_ID", nullable = false)	private FileArtifact file;		@Column(name = "FILE_PATH", columnDefinition = "VARCHAR(255)")	private String filePath;	public FileArtifact getFile() {		return file;	}	public void setFile(final FileArtifact file) {		this.file = file;	}	public String getFilePath() {		return filePath;	}	public void setFilePath(final String filePath) {		this.filePath = filePath;	}	public FileDependencyObject() {		super(DependencyObjectType.FILE_DEPENDENCY.getValue());			}	@Override	public String getDependencyObjectName() {		return this.file.getName();	}		public String toString(){		return this.filePath;	}	}